let process_path_create_whitelist = process_create_whitelist | project process_path;
let process_path_access_whitelist = process_access_whitelist | project process_path;
let process_path_dns_whitelist = dns_whitelist | project process_path;
let process_path_file_create_whitelist = file_create_whitelist | project process_path;
let process_path_image_load_whitelist = image_load_whitelist | project process_path;
let process_path_network_whitelist = network_whitelist | project process_path;
let process_path_pipe_whitelist = pipe_whitelist | project process_path;
let process_path_registry_whitelist = registry_whitelist | project process_path;
Sysmon
| where UserName contains @"{user_name_value}"
| where isnotempty(technique_name)
| where process_path !in~ (process_path_create_whitelist) and process_path !in~ (process_path_access_whitelist) and process_path !in~ (process_path_dns_whitelist) and process_path !in~ (process_path_file_create_whitelist) and process_path !in~ (process_path_image_load_whitelist) and process_path !in~ (process_path_network_whitelist) and process_path !in~ (process_path_pipe_whitelist) and process_path !in~ (process_path_registry_whitelist)
| summarize count() by technique_name, bin(TimeGenerated, 1h)

let process_path_create_whitelist = process_create_whitelist | project process_path;
let process_path_access_whitelist = process_access_whitelist | project process_path;
let process_path_dns_whitelist = dns_whitelist | project process_path;
let process_path_file_create_whitelist = file_create_whitelist | project process_path;
let process_path_image_load_whitelist = image_load_whitelist | project process_path;
let process_path_network_whitelist = network_whitelist | project process_path;
let process_path_pipe_whitelist = pipe_whitelist | project process_path;
let process_path_registry_whitelist = registry_whitelist | project process_path;
Sysmon
| where isnotempty(technique_name)
| where process_path !in~ (process_path_create_whitelist) and process_path !in~ (process_path_access_whitelist) and process_path !in~ (process_path_dns_whitelist) and process_path !in~ (process_path_file_create_whitelist) and process_path !in~ (process_path_image_load_whitelist) and process_path !in~ (process_path_network_whitelist) and process_path !in~ (process_path_pipe_whitelist) and process_path !in~ (process_path_registry_whitelist)
| summarize c=count() by Computer, UserName
| sort by c desc
| project Computer, UserName, c

let process_ppath_whitelist = process_create_whitelist | project process_parent_path;
let process_path_whitelist = process_create_whitelist | project process_path;
let command_line_whitelist = process_create_whitelist | project replace("'", "", replace('"', '', process_command_line));
let hash_whitelist = process_create_whitelist | project hash_sha256;
Sysmon
| where RenderedDescription contains "Process create" and UserName == @"{user_name_value}"
| where process_parent_path !in~ (process_ppath_whitelist) and process_path !in~ (process_path_whitelist) and replace('"', '', tostring(process_command_line)) !in~ (command_line_whitelist) and hash_sha256 !in~ (hash_whitelist)
| project TimeGenerated, technique_id, technique_name, phase_name, Computer, UserName, process_parent_path, process_path, file_name, process_parent_command_line, process_command_line, process_parent_guid, process_guid, hash_sha256, process_id, process_parent_id

let process_path_whitelist = process_access_whitelist | project process_path;
let target_process_path_whitelist = process_access_whitelist | project target_process_path;
let process_granted_access_whitelist = process_access_whitelist | project process_granted_access;
Sysmon
| where RenderedDescription contains "Process access" and UserName == @"{user_name_value}"
| where process_path !in~ (process_path_whitelist) and target_process_path !in~ (target_process_path_whitelist) and process_granted_access !in~ (process_granted_access_whitelist)
| project TimeGenerated, technique_id, technique_name, phase_name, Computer, process_path, target_process_path, process_granted_access, target_process_guid, process_id, target_process_id

let file_name_whitelist = file_create_whitelist | project file_name;
let file_path_whitelist = file_create_whitelist | project file_path;
let proc_path_whitelist = file_create_whitelist | project process_path;
Sysmon
| where RenderedDescription contains "File create" and UserName == @"{user_name_value}"
| where process_path !in~ (proc_path_whitelist) and file_name !in~ (file_name_whitelist)
| project TimeGenerated, technique_id, technique_name, phase_name, Computer, process_path, file_name, process_guid, process_id

let process_path__whitelist = image_load_whitelist | project process_path;
let driver_loaded_whitelist = image_load_whitelist | project driver_loaded;
let driver_signed_whitelist = image_load_whitelist | project driver_is_signed;
let drv_signature_whitelist = image_load_whitelist | project driver_signature;
let signat_status_whitelist = image_load_whitelist | project driver_signature_status;
Sysmon
| where RenderedDescription contains "Image loaded" and UserName == @"{user_name_value}"
| where process_path !in~ (process_path__whitelist) and module_loaded !in~ (driver_loaded_whitelist) and module_is_signed !in~ (driver_signed_whitelist) and module_signature !in~ (drv_signature_whitelist) and module_signature_status !in~ (signat_status_whitelist)
| project TimeGenerated, technique_id, technique_name, phase_name, Computer, UserName, process_path, module_loaded, module_is_signed, module_signature, module_signature_status, process_id, process_guid

let process_path_whitelist = network_whitelist | project process_path;
let src_ip_whitelist = network_whitelist | project src_ip;
let dst_ip_whitelist = network_whitelist | project dst_ip;
let dst_port_whitelist = network_whitelist | project dst_port;
Sysmon
| where RenderedDescription contains "Network connect" and UserName == @"{user_name_value}"
| where process_path !in~ (process_path_whitelist) and src_ip !in~ (src_ip_whitelist) and dst_ip !in~ (dst_ip_whitelist) and dst_port !in~ (dst_port_whitelist)
| project TimeGenerated, technique_id, technique_name, phase_name, Computer, UserName, process_path, process_id, process_guid, src_ip, dst_ip, dst_port, src_host_name, dst_host_name

let process_path_whitelist = remote_thread_whitelist | project process_path;
let target_process_path_whitelist = remote_thread_whitelist | project target_process_path;
Sysmon
| where EventID == 8 and UserName == @"{user_name_value}"
| where process_path !in~ (process_path_whitelist) and target_process_path !in~ (target_process_path_whitelist)
| project TimeGenerated, technique_id, technique_name, phase_name, Computer, target_process_path, thread_new_id, process_guid, process_parent_guid

let event_type_whitelist = registry_whitelist | project event_type;
let process_path_whitelist = registry_whitelist | project process_path;
let registry_key_path_whitelist = registry_whitelist | project registry_key_path;
Sysmon
| where EventID == 12 and UserName == @"{user_name_value}"
| where process_path !in~ (process_path_whitelist) and EventType !in~ (event_type_whitelist) and registry_key_path !in~ (registry_key_path_whitelist)
| project TimeGenerated, technique_id, technique_name, phase_name, Computer, process_path, process_id, process_guid, registry_key_path

let process_path_whitelist = pipe_whitelist | project process_path;
let pipe_name_whitelist = pipe_whitelist | project pipe_name;
Sysmon
| where (EventID == 17 or EventID==18) and UserName == @"{user_name_value}"
| where process_path !in~ (process_path_whitelist) and pipe_name !in~ (pipe_name_whitelist)
| project TimeGenerated, technique_id, technique_name, phase_name, Computer, pipe_name, process_path, process_guid, process_id
